	<script>
		function deleteFromCart(flag,id,pn,pa,tv,cc){
			ga('send', 'event', { eventCategory: 'Checkout Page', eventAction: 'Product Removed', eventLabel: pn, eventValue: pa});
			if(cc!=0){
				$.post('couponTestAll.php',{'pa':pa,'fl':tv}, function(res){
					//alert(res);
						$t=res.charAt(0);
							if(res=='Invalid Coupon Code'){
								$('.modal-body h5').text('Your current coupon will be invalid, if you cancel this item.');
								$("#couponInfoAll").modal('show');
								$("#cAllF").val(flag);
								$("#cAllId").val(id);
								$("#cAllAmount").val(0);
								ctotal=0;
							}else if($t=='d'){
								res=res.substr(1);
								$('.modal-body h5').html('If you cancel this product then your current coupon amount change with <i class="fa fa-rupee"></i> '+res+' .');
								ctotal=res;
								$("#couponInfoAll").modal('show');
								$("#cAllF").val(flag);
								$("#cAllId").val(id);
								$("#cAllAmount").val(res);
							}else{
								$('.modal-body h5').html('Do you want to remove product from cart?');
								ctotal=res;
								$("#couponInfoAll").modal('show');
								$("#cAllF").val(flag);
								$("#cAllId").val(id);
								$("#cAllAmount").val('no');
							}
						});
			}else{
				$.post('deleteProduct.php',{'f':flag, 'id':id,'ctotal':'no'},function(res){
					if(res=='done')
					{
						//alert(res);
						window.location.reload();
					}
					else
					{
						console.log(res);
					}
				});
			}
		}
		function cancelOrderAll(f){
			if(f==0){
				$("#couponInfoAll").modal('hide');
			}else{	
			flag=$("#cAllF").val();
			id=$("#cAllId").val();
			ctotal=$("#cAllAmount").val();
			$("#couponInfoAll").modal('hide');
				$.post('deleteProduct.php',{'f':flag, 'id':id,'ctotal':ctotal},function(res){
					if(res=='done')
					{
						//alert(res);
						window.location.reload();
					}
					else
					{
						console.log(res);
					}
				});
			}
		}
